import React, { useState } from 'react';
import { Icon } from '@iconify/react';
import Slider from 'react-slick';
import Modal from './Modal';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Projects.css';

const projects = [
  {
    title: "Smooth Smoothies",
    description: "A multi-column, flexbox-based page layout and hero image with a tinted overlay.",
    image: "/images/smooth-smoothies.jpg",
  },
  {
    title: "Snazzy Fashion",
    description: "A web page for a fashion retailer with a background video that plays in a continuous loop.",
    image: "/images/snazzy-fashion.jpg",
  },
  {
    title: "Hero Block Gallery",
    description: "A gallery of hero block elements with split-screen layouts and duotone images.",
    image: "/images/hero-block-gallery.jpg",
  },
];

export default function Projects() {
  const [selectedProject, setSelectedProject] = useState(null);

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: { slidesToShow: 1 }
      }
    ]
  };

  return (
    <section className="projects-section">
      <Slider {...sliderSettings} className="projects-slider">
        {projects.map((project, index) => (
          <div className="project-card" key={index}>
            <img src={project.image} alt={project.title} className="project-image" />
            <h3 className="project-title">{project.title}</h3>
            <p className="project-description">{project.description}</p>
            <button
              className="project-button"
              onClick={() => setSelectedProject(project)}
            >
              View project <Icon icon="ic:round-arrow-forward" />
            </button>
          </div>
        ))}
      </Slider>

      {selectedProject && (
        <Modal onClose={() => setSelectedProject(null)}>
          <div className="modal-content">
            <h2>{selectedProject.title}</h2>
            <img src={selectedProject.image} alt={selectedProject.title} className="modal-image" />
            <p>{selectedProject.description}</p>
            <button onClick={() => setSelectedProject(null)} className="close-btn">Close</button>
          </div>
        </Modal>
      )}
    </section>
  );
}
