sourcesContent:
const express = require('express');
const nodemailer = require('nodemailer');
const router = express.Router();

router.post('/send-email', async (req, res) => {
  const { name, email } = req.body;

  // Create transporter using Gmail SMTP
  const transporter = nodemailer.createTransport({
    service: 'gmail',
    auth: {
      user: 'yourgmail@gmail.com',         // 游대 Replace with your Gmail
      pass: 'your_app_password'            // 游대 Use Google App Password (not normal password)
    }
  });

  const mailOptions = {
    from: 'yourgmail@gmail.com',          // 游대 Same Gmail as above
    to: email,
    subject: 'Thanks for contacting me!',
    text: `Hi ${name},\n\nThanks for visiting my portfolio page. I will get back to you soon.\n\nBest regards,\n[Your Name]`
  };

  try {
    await transporter.sendMail(mailOptions);
    res.status(200).json({ message: 'Auto-reply email sent!' });
  } catch (error) {
    console.error('Failed to send email:', error);
    res.status(500).json({ error: 'Email send failed' });
  }
});

module.exports = router;
